					*astate = S_META;
					return 0;
				case '^':
					*astate = S_CTRL;
					return 0;
				case 'n':
					*cp = '\n';
					*astate = S_GROUND;
					return UNVIS_VALID;
				case 'r':
					*cp = '\r';
					*astate = S_GROUND;
					return UNVIS_VALID;
				case 'b':
					*cp = '\b';
					*astate = S_GROUND;
					return UNVIS_VALID;
				case 'a':
					*cp = '\007';
					*astate = S_GROUND;
					return UNVIS_VALID;
				case 'v':
					*cp = '\v';
					*astate = S_GROUND;
					return UNVIS_VALID;
				case 't':
					*cp = '\t';
					*astate = S_GROUND;
					return UNVIS_VALID;
				case 'f':
					*cp = '\f';
					*astate = S_GROUND;
					return UNVIS_VALID;
				case 's':
					*cp = ' ';
					*astate = S_GROUND;
					return UNVIS_VALID;
				case 'E':
					*cp = '\033';
					*astate = S_GROUND;
					return UNVIS_VALID;
