				min = 1;
				range = 31;
				goto strptime_numeric_range;
			case 'D':
				s = strptime(s, "%m/%d/%y", _tm);
				if (!s) { return 0; }
				break;
			case 'H':
				dest = &_tm->tm_hour;
				min = 0;
				range = 24;
				goto strptime_numeric_range;
			case 'I':
				dest = &_tm->tm_hour;
				min = 1;
				range = 12;
				goto strptime_numeric_range;
			case 'j':
				dest = &_tm->tm_yday;
				min = 1;
				range = 366;
				goto strptime_numeric_range;
			case 'm':
				dest = &_tm->tm_mon;
				min = 1;
				range = 12;
				adj = 1;
				goto strptime_numeric_range;
			case 'M':
				dest = &_tm->tm_min;
				min = 0;
				range = 60;
				goto strptime_numeric_range;
