				if (!s) { return 0; }
				break;
			case 'R':
				s = strptime(s, "%H:%M", _tm);
				if (!s) { return 0; }
				break;
			case 'S':
				dest = &_tm->tm_sec;
				min = 0;
				range = 61;
				goto strptime_numeric_range;
			case 'T':
				s = strptime(s, "%H:%M:%S", _tm);
				if (!s) { return 0; }
				break;
			case 'U':
			case 'W':
