				if (*s == '+') { s++; }
				else if (*s == '-') { neg = 1; s++; }
				if (!isdigit(*s)) { return 0; }
				for (*dest = i = 0; i < w && isdigit(*s); i++) { *dest = *dest * 10 + *s++ - '0'; }
