	aHigh = (uint16_t)(a >> 16);
	bLow = (uint16_t)b;
	bHigh = (uint16_t)(b >> 16);
	z1 = ((uint32_t)aLow) * bLow;
	zMiddleA = ((uint32_t)aLow) * bHigh;
	zMiddleB = ((uint32_t)aHigh) * bLow;
	z0 = ((uint32_t)aHigh) * bHigh;
	zMiddleA += zMiddleB;
	z0 += (((uint32_t)(zMiddleA < zMiddleB)) << 16) + (zMiddleA >> 16);
