	if (!_digit) { return INADDR_NONE; }
	else if (pp >= parts + 4 || val > 0xffU) { return INADDR_NONE; }
	else if (*cp == '.') {
		*pp++ = val, cp++;
		goto inet_network_again;
	}
	if (*cp && (!isspace(*cp & 0xff))) { return INADDR_NONE; }
	*pp++ = val;
	n = (in_addr_t)(pp - parts);
	if (n > 4U) { return INADDR_NONE; }
